import axios from 'axios';
import {clientConstants, apiUrl} from '../constants';


export const submitClientData = (data) => {
    return (dispatch) => {
        dispatch(request());
        return axios.post(`${apiUrl}addUserData`, data)
            .then(({data}) => {
                if (data.status) {
                    dispatch(success(data.data));
                } else {
                    dispatch(failure(data.message));
                }
            })
            .catch(error => {
                throw(error);
                dispatch(failure(error.message));
            });
    }

    function request() {
        return {type: clientConstants.CLIENTDATA_SUBMIT_REQUEST}
    }

    function success(user) {
        return {type: clientConstants.CLIENTDATA_SUBMIT_SUCCESS, user}
    }

    function failure(error) {
        return {type: clientConstants.CLIENTDATA_SUBMIT_FAILURE, error}
    }
};
