import React, {Component} from 'react';
import {connect} from 'react-redux';
import * as actions from '../../actions';
import "./Dashboard.scss";
import {Link} from "react-router-dom";
import LoadingOverlay from 'react-loading-overlay';
import {faEdit, faTrash, faChevronRight} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

import {
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    Dropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem,
    InputGroup,
    InputGroupAddon,
    InputGroupText,
    FormInput,
    Button,
    Modal,
    ModalBody,
    ModalHeader,
    ModalFooter,
    Container,
    Row,
    Col,
    FormCheckbox
} from "shards-react";

class Dashboard extends Component {
    state = {
        userName: "",
        userPassword: "",
        rememberMe: false,
        loading: false,
        showConfirmDeleteModal: false,
        showUserDataModel: false,
        usersData: [1, 2, 3, 4, 5, 6, 7],

        affiliatedWithNapaStore: false,
        anInstallerCustomer: false,

        napaNewElectrical: false,
        wilson: false,
        premiumPlus: false,
        premiumSteering: false,
        powerSupport: false,
        newSteering: false,

        selectedUserData: null
    };


    componentWillMount() {
    }

    componentDidMount() {
        const userStr = localStorage.getItem('user_data');
        if (userStr) {
            try {
                const user = JSON.parse(userStr);
                if (user.id && user.email) {
                    this.getUsersDataList();
                } else {
                    this.props.history.push("/admin/login");
                }
            } catch (e) {
                this.props.history.push("/admin/login");
            }
        } else {
            this.props.history.push("/admin/login");
        }
    }

    getUsersDataList = () => {
        const {dispatch} = this.props;
        dispatch(actions.userDataList({})).then(() => {

        });
    }

    deleteUserData = (userDataId) => {
