import React, {Component} from 'react';
import {connect} from 'react-redux';
import * as actions from '../../actions';
import BackgroundImage from '../../components/BackgroundImage/BackgroundImage';
import "./Spin.scss";
import {apiUrl} from "../../constants";
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css";
import "shards-ui/dist/css/shards.min.css"
import {
    FormCheckbox,
    FormInput,
    Container,
    Row,
    Col,
    Button
} from "shards-react";
import $ from "jquery";

class Spin extends Component {
    state = {
        loading: false,

        affiliatedWithNapaStore: false,
        anInstallerCustomer: false,

        napaNewElectrical: false,
        wilson: false,
        premiumPlus: false,
        premiumSteering: false,
        powerSupport: false,
        newSteering: false,

        items: ['item1.png', 'item2.png', 'item3.png', 'item4.png', 'item5.png']
    };

    credits = 15;
    spinning = 3;
    spinCounts = [0, 0, 0];
    slotsTypes = {
        'cherry': [2, 5, 10],
        'orange': [0, 15, 30],
        'prune': [0, 40, 50],
        'bell': [0, 50, 80],
        'bar1': [0, 0, 100],
        'bar2': [0, 0, 150],
        'bar3': [0, 0, 250],
        'seven': [0, 0, 500],
        'anybar': [0, 0, 80]
    };
    slots = [
        ['orange', 'bell', 'orange', 'bar2', 'prune', 'orange',
            'bar3', 'prune', 'orange', 'bar1', 'bell', 'cherry', 'orange',
            'prune', 'bell', 'bar1', 'cherry', 'seven', 'orange', 'prune',
            'orange', 'bell', 'orange'],
        ['cherry', 'prune', 'orange', 'bell', 'bar1', 'cherry', 'prune',
            'bar3', 'cherry', 'bell', 'orange', 'bar1', 'seven', 'cherry',
            'bar2', 'cherry', 'bell', 'prune', 'cherry', 'orange', 'cherry',
            'prune', 'orange'],
        ['cherry', 'orange', 'bell', 'prune', 'bar2', 'cherry', 'prune',
            'orange', 'bar3', 'cherry', 'bell', 'orange', 'cherry', 'orange',
            'cherry', 'prune', 'bar1', 'seven', 'bell', 'cherry', 'cherry',
            'orange', 'bell'],
    ];


    constructor(props) {
        super(props);

        this.startSlot();

        $('#slot-trigger')
            .bind('mousedown', function () {
                $(this).addClass('slot-triggerDown');
            })
            .bind('click', this.spinCounts);

        $(document).bind('mouseup', function () {
            $('#slot-trigger').removeClass('slot-triggerDown');
        });

        $('#wheel1 img:first').css('top', -(parseInt(Math.random() * 23) * 44) + 'px');
        $('#wheel2 img:first').css('top', -(parseInt(Math.random() * 23) * 44) + 'px');
        $('#wheel3 img:first').css('top', -(parseInt(Math.random() * 23) * 44) + 'px');
    }
    componentWillReceiveProps(nextProps, nextContext) {

    }

    componentWillMount() {

    }

    componentDidMount() {

    }

    stopSpin(slot) {
        $('.wheel' + slot)
            .find('img:last')
            .hide()
            .end()
            .find('img:first')
            .animate({
                top: -this.spinCounts[slot - 1] * 44
            }, {
                duration: 500,
                easing: 'elasticOut',
                complete: function () {

                    this.spinning--;

                    if (this.spinning <= 0) {
                        this.endSpin();
                    }

                }
            });
    }

    endSpin() {
        var slotType = this.slots[0][this.spinCounts[0]],
            matches = 1,
            barMatch = /bar/.test(slotType) ? 1 : 0,
            winnedCredits = 0,
            waitToSpin = 10;

        if (slotType == this.slots[1][this.spinCounts[1]]) {

            matches++;

            if (slotType == this.slots[2][this.spinCounts[2]]) {
                matches++;
            } else if (barMatch != 0 && /bar/.test(this.slots[2][this.spinCounts[2]])) {
                barMatch++;
            }

        } else if (barMatch != 0 && /bar/.test(this.slots[1][this.spinCounts[1]])) {

            barMatch++;

            if (/bar/.test(this.slots[2][this.spinCounts[2]])) {
                barMatch++;
            }

        }

        if (matches != 3 && barMatch == 3) {
            slotType = 'anybar';
            matches = 3;
        }

        var winnedCredits = this.slotsTypes[slotType][matches - 1];

        if (winnedCredits > 0) {
            // addCredit(winnedCredits);
            waitToSpin = 410 + winnedCredits;
        }

        setTimeout(function () {

            if (this.credits == 0) {
                this.endSlot();
            } else {
                $('#slot-trigger').removeClass('slot-triggerDisabled');
                this.spinning = false;
            }

        }, waitToSpin);
    }

    spin() {
        // this.blur();
debugger;
        if (this.spinning == false) {

            $('#slot-machine .arm').animate({top: '45px', height: '2%'});
            $('#slot-machine .arm .knob').animate({top: '-20px', height: '20px'});
            $('#slot-machine .arm-shadow').animate({top: '40px'}, 380);
            $('#slot-machine .ring1 .shadow, #slot-machine .ring2 .shadow').animate({top: '50%', opacity: 1});

            this.spinning = 3;
            this.credits--;

            $('#slot-credits').html(this.credits);

            this.spinCounts[0] = parseInt(Math.random() * 23);
            this.spinCounts[1] = parseInt(Math.random() * 23);
            this.spinCounts[2] = parseInt(Math.random() * 23);

            $('#slot-trigger').addClass('slot-triggerDisabled');

            $('img.slotSpinAnimation').show();

            $('#wheel1 img:first').css('top', -(this.spinCounts[0] * 44 + 16) + 'px');
            $('#wheel2 img:first').css('top', -(this.spinCounts[1] * 44 + 16) + 'px');
            $('#wheel3 img:first').css('top', -(this.spinCounts[2] * 44 + 16) + 'px');

            setTimeout(function () {
                $('#slot-machine .arm').animate({top: '-25px', height: '50%', overflow: 'visible'});
                $('#slot-machine .arm .knob').animate({top: '-15px', height: '16px'});
                $('#slot-machine .arm-shadow').animate({top: '13px'});
                $('#slot-machine .ring1 .shadow, #slot-machine .ring2 .shadow').animate({top: '0', opacity: 0});
            }, 500);

            setTimeout(function () {
                this.stopSpin(1);
            }, 1500 + parseInt(1500 * Math.random()));

            setTimeout(function () {
                this.stopSpin(2);
            }, 1500 + parseInt(1500 * Math.random()));

            setTimeout(function () {
                this.stopSpin(3);
            }, 1500 + parseInt(1500 * Math.random()));

        }

        return false;
    }

    startSlot() {

        this.spinning = false;

        $('#slot-trigger').removeClass('slot-triggerDisabled');

        // this.blur();

        return false;

    }

    endSlot() {

        $('#slot-block').show();
        $('#slot-credits').text('VERLOREN!!!');

        setInterval(this.blink($('#slot-credits')), 1000);

    }

    blink(element) {
        if (element) {
            element.animate({opacity: 0}, 200, 'linear', function () {
                $(this).animate({opacity: 1}, 200);
            });
        }
    }

    render() {


        return (
            <div className="spin-page-container">
                <img className={'spin-bg-image'} src={'images/spinbackground.png'}/>
                <Container id={'slot-wheels'}>
                    <Row>
                        <Col className={'wheel'} id={'wheel1'}>
                            <div className="overlay"></div>
                            {this.state.items.map((item, key) => {
                                return (
                                    <img src={'images/' + item}/>
                                )
                            })}
                        </Col>
                        <Col className={'wheel'} id={'wheel2'}>
                            <div className="overlay"></div>
                            {this.state.items.map((item, key) => {
                                return (
                                    <img src={'images/' + item}/>
                                )
                            })}
                        </Col>
                        <Col className={'wheel'} id={'wheel3'}>
                            <div className="overlay"></div>
                            {this.state.items.map((item, key) => {
                                return (
                                    <img src={'images/' + item}/>
                                )
                            })}
                        </Col>
                    </Row>
                </Container>

                <div className={'btn-container'}>
                    <Button onClick={this.spin}>SPIN NOW</Button>

                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {

    const {user} = state.user;
    return {
        user
    };
}

export default connect(mapStateToProps)(Spin);
