import {userConstants} from '../constants';

export default (state = {}, action) => {
    switch (action.type) {
        case userConstants.LOGIN_REQUEST:
            return {
                ...state,
                loginState: -1
            };
        case userConstants.LOGIN_SUCCESS:
            return {
                ...state,
                loginState: 1,
                user: action.user
            };
        case userConstants.LOGIN_FAILURE:
            return {
                ...state,
                loginState: 0,
                error: action.error
            };

        default:
            return state
    }
};
