import axios from 'axios';
import {clientConstants, apiUrl, userConstants} from '../constants';


export const submitUserData = (data) => {
    return (dispatch) => {
        dispatch(request());
        return axios.post(`${apiUrl}addUserData`, data)
            .then(({data}) => {
                if (data.status) {
                    dispatch(success(null));
                } else {
                    dispatch(failure(data.message));
                }
            })
            .catch(error => {
                throw(error);
                dispatch(failure(error.message));
            });
    }

    function request() {
        return {type: clientConstants.USERDATA_SUBMIT_REQUEST}
    }

    function success(user) {
        return {type: clientConstants.USERDATA_SUBMIT_SUCCESS, user}
    }

    function failure(error) {
        return {type: clientConstants.USERDATA_SUBMIT_FAILURE, error}
    }
};


export const userDataList = (data) => {
    return (dispatch) => {
        dispatch(request([]));
        return axios.get(`${apiUrl}/userDataList`, data)
            .then(({data}) => {
                if (data.status) {
                    dispatch(success(data.data));
                } else {
                    dispatch(failure(data.message));
                }
            })
            .catch(error => {
                throw(error);
                dispatch(failure(error.message));
            });
    }

    function request(users) {
        return {type: userConstants.LOGIN_REQUEST, users}
    }

    function success(users) {
        return {type: userConstants.LOGIN_SUCCESS, users}
    }

    function failure(error) {
        return {type: userConstants.LOGIN_FAILURE, error}
    }
};
