import {clientConstants} from '../constants';

export default (state = {}, action) => {
    switch (action.type) {
        case clientConstants.CLIENTDATA_SUBMIT_REQUEST:
            return {
                ...state,
                dataAddingStatus: -1
            };
        case clientConstants.CLIENTDATA_SUBMIT_SUCCESS:
            return {
                ...state,
                dataAddingStatus: 1,
                user: action.user
            };
        case clientConstants.CLIENTDATA_SUBMIT_FAILURE:
            return {
                ...state,
                dataAddingStatus: 0,
                error: action.error
            };

        default:
            return state
    }
};
