import React, {Component} from 'react';
import {connect} from 'react-redux';
import * as actions from '../../actions';
import BackgroundImage from '../../components/BackgroundImage/BackgroundImage';
import "./Spin.scss";
import {apiUrl} from "../../constants";
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css";
import "shards-ui/dist/css/shards.min.css"
import {
    FormCheckbox,
    FormInput,
    Container,
    Row,
    Col,
    Button
} from "shards-react";

class Spin extends Component {
    state = {
        loading: false,
    };


    componentWillReceiveProps(nextProps, nextContext) {
        this.setState({
            affiliatedWithNapaStore: false,
            anInstallerCustomer: false,

            napaNewElectrical: false,
            wilson: false,
            premiumPlus: false,
            premiumSteering: false,
            powerSupport: false,
            newSteering: false,

            items: ['item1.png', 'item2.png', 'item3.png', 'item4.png', 'item5.png']
        });
    }

    componentWillMount() {

    }

    componentDidMount() {

    }


    render() {


        return (
            <div className="spin-page-container">
                <img className={'spin-bg-image'} src={'images/spinbackground.png'}/>
                <Container className={'slot-wheels'}>
                    <Row>
                        <Col className={'wheel1'}>
                            <div className="overlay"></div>
                            {this.state.items.map((item, key) => {
                                return (
                                    <img src={'images/' + item}/>
                                )
                            })}
                        </Col>
                        <Col className={'wheel2'}>
                            <div className="overlay"></div>
                        </Col>
                        <Col className={'wheel3'}>
                            <div className="overlay"></div>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

const mapStateToProps = (state) => {

    const {user} = state.user;
    return {
        user
    };
}

export default connect(mapStateToProps)(Spin);
